<style>    #sum_total{        color:red;        font-weight:bold;        border-bottom: SOLID;    }    #summary{        border-style:solid;        font-size:12px;    }    #summary th{        padding:8px;    }    #summary td{        padding:5px;    }</style><div id="summary">    <?php    if($_SESSION['selected_grade']=='all') {        $selected_grade='';    }else {        $selected_grade=$_SESSION['selected_grade'];    }    include("config.php");    $query="SELECT supplier_type,sum(weight) FROM sup_deliveries  where wp_grade like '%$selected_grade%' and supplier_type !='#N/A' and supplier_type !='0' and month_delivered='".date('F')."' and year_delivered='".$_SESSION['yearcriteria']."'  and branch_delivered like '%".$_SESSION['supplier_branch']."%' group by supplier_type;";    $result=mysql_query($query);    $query2="SELECT sum(weight) FROM sup_deliveries  where wp_grade like '%".$selected_grade."%' and month_delivered='".date('F')."' and year_delivered='".$_SESSION['yearcriteria']."'and branch_delivered like '%".$_SESSION['supplier_branch']."%' ";    $result2=mysql_query($query2);    $row2 = mysql_fetch_array($result2);    $overall_total=$row2['sum(weight)'];    echo '<table border=1>';    echo "<thead>";    echo "<th>Supplier Type</th>";    echo "<th>Deliveries(KG)</th>";    echo "<th>Percentage</th>";    echo "<th>Head Count</th>";    echo "</thead>";    $total=0;    $total_percentage=0;    $total_head_count=0;    while($row = mysql_fetch_array($result)) {        $total+=$row['sum(weight)'];        echo "<tr class='light'>";        echo "<td>".$row['supplier_type']."</td>";        echo "<td>".number_format($row['sum(weight)'],0)."</td>";        $query3="SELECT sum(weight),count(supplier_id) FROM sup_deliveries  where wp_grade like '%$selected_grade%' and supplier_type='".$row['supplier_type']."' and month_delivered='".date('F')."' and year_delivered='".$_SESSION['yearcriteria']."' and branch_delivered like '%".$_SESSION['supplier_branch']."%'";        $result3=mysql_query($query3);        $row3 = mysql_fetch_array($result3);        $query4="SELECT sum(weight),count(supplier_id) FROM sup_deliveries  where wp_grade like '%$selected_grade%' and supplier_type='".$row['supplier_type']."' and month_delivered='".date('F')."'  and year_delivered='".$_SESSION['yearcriteria']."' and branch_delivered like '%".$_SESSION['supplier_branch']."%'";        $result4=mysql_query($query4);        $row4 = mysql_fetch_array($result4);        $total_type=$row3['sum(weight)'];        $percentage=($total_type/$overall_total)*100;        $percentage=number_format($percentage,0);        $total_percentage+=$percentage;        $query5="SELECT count(supplier_id) FROM supplier_details where classification='".$row['supplier_type']."'  ";        $result5=mysql_query($query5);        $row5 = mysql_fetch_array($result5);        echo "<td>".$percentage."%"."</td>";        echo "<td>".number_format($row5['count(supplier_id)'],0)."</td>";        $total_head_count+=$row5['count(supplier_id)'];        echo "</tr>";    }    echo "<thead id='sum_total'>";    echo "<td>TOTAL</td>";    echo "<td>".number_format($total,0)."</td>";    echo "<td>".$total_percentage."%</td>";    echo "<td>".$total_head_count."</td>";    echo "</thead>";    echo "</table>";    ?></div>